import React, {useState} from 'react';




export default function TextForm(props) {
    const [text, setText] = useState("");
    // setText("hey man");
    // console.log(text);
    // let input_text = "hi";
    let input_text = document.getElementById('input-text').value;
    const handleOnChange = (event)=>{
        // it will handle text value inside the state if any changing occurs inside this
        setText(event.target.value);
    }
    const UpperCase = ()=>{
        setText(input_text.toUpperCase());
        // console.log("Clicked",text);
    }
    const LowerCase = ()=>{
        setText(input_text.toLowerCase());
        // console.log("clicked", text);
    }

    return (
        <div className='my-4 container'>
            <h2>{props.heading}</h2>
            <div className="input-group">
                <textarea type="text" className="form-control" id="input-text" placeholder="Enter your text" value={text} onChange={handleOnChange} cols="200" rows="10" />
            </div>
            <div className='container'>
                <button className="btn btn-outline-secondary" id='upper-case' type="button" onClick={UpperCase}>UpperCase</button>
                <button className="btn btn-outline-secondary" id='lower-case' type="button" onClick={LowerCase}>LowerCase</button>
                <button className="btn btn-outline-secondary" id='lower-case' type="button">Reset</button>
            </div>

        </div>
    )
}
