// import logo from './logo.svg';
import { useState } from 'react';
import './App.css';
import './body.css'
import Navbar from './components/Navbar';
import TextForm from './components/TextForm';
function App() {
  const [mode, setMode] = useState({
    color: 'black',
    backgroundColor: 'white',
    transition: 'all 1s'
    // border: '1px solid black'
  });
  const changeColorMode = () => {
    if (mode === "light") {
      setMode("dark");
    }
    else {
      setMode('light');
    }
  }
  return (
    <>
      <div>
        <Navbar title='TextUtils' name="Converter" mode={mode} changeMode={changeColorMode} />
        <TextForm heading="Enter the text to convert in UpperCase or LowerCase." mode={mode} />
      </div>
    </>
  );
}

export default App;
