import React, {useState} from 'react';

export default function TextForm(props) {
    const [text, setText] = useState("Enter Your Text");
    // setText("hey man");
    // console.log(text);
    const UpperCase = ()=>{
        setText(document.getElementById('input-text').innerText.toUpperCase());
        console.log("Clicked",text);
    }
    const LowerCase = ()=>{
        setText(document.getElementById('input-text').innerText.toLowerCase());
        console.log("clicked", text);
    }
    return (
        <div className='my-4 container'>
            <h2>{props.heading}</h2>
            <div className="input-group">
                <input type="text" className="form-control" id="input-text" placeholder="Enter your text" aria-label="Recipient's username with two button addons" />
                <button className="btn btn-outline-secondary" id='upper-case' type="button" onClick={UpperCase}>UpperCase</button>
                <button className="btn btn-outline-secondary" id='lower-case' type="button" onClick={LowerCase}>LoweCase</button>
            </div>
            <div className='output'>
                <textarea cols="300" rows="10" value={text}></textarea>
                <input type="button" value="C" title='Copy to clipboard' />
            </div>
        </div>
    )
}
