import React, {useState} from 'react';

export default function textForm(props) {
    const [text, setText] = useState("Enter Your Text");
    setText("hey man");
    return (
        <div className='my-4 container'>
            <h2>{props.heading}</h2>
            <div className="input-group">
                <input type="text" className="form-control input-text" placeholder="Enter your text" aria-label="Recipient's username with two button addons" />
                <button className="btn btn-outline-secondary" id='upper-case' type="button">UpperCase</button>
                <button className="btn btn-outline-secondary" id='lower-case' type="button">LoweCase</button>
            </div>
            <div className='output'>
                <p id='output-text' >Your output will be here!</p>
                <input type="button" value="C" title='Copy to clipboard' />
            </div>
        </div>
    )
}
