let upper_btn = document.getElementById('upper-case');
let lower_btn = document.getElementById('lower-btn');
let input_text = document.getElementsByClassName('input-text')[0];
let output_text = document.getElementById('output-text')
const text = upper_btn.addEventListener("click", ()=>{
    return input_text.innerText.toUpperCase();
})
output_text.innerText = text;
console.log("hi there", text[0]);
