// import logo from './logo.svg';
import { useState } from 'react';
import './App.css';
import './body.css'
import Alert from './components/Alert';
import Navbar from './components/Navbar';
import TextForm from './components/TextForm';
function App() {
  const [alert, setAlert] = useState(null);
  const [text, setText] = useState(null);
  const [mode, setMode] = useState({
    color: 'black',
    backgroundColor: 'white',
    transition: 'all 1s'
  });
  const showAlert = (message, type) => {
    setAlert({
      msg: message,
      type: type
    })
    setTimeout(() => {
      setAlert(null);
    }, 3000)
  }
  const setBackground = (clr) => {
    if (clr === 'üü¢') {
      setMode({
        color: 'white',
        backgroundColor: 'green',
        transition: 'all 1s',
        borderColor: "white",
      });
      // setText("üü¢");
      showAlert("Green Mode Enabled", "Success");
      document.body.style.backgroundColor = "green";
      document.body.style.transition = "all 1s";
    }
    else if (clr === 'üü°') {
      setMode({
        color: 'black',
        backgroundColor: 'yellow',
        transition: 'all 1s',
        borderColor: "black"
      });
      // setText("üü¢");
      showAlert("Red Mode Enabled", "Success");
      document.body.style.backgroundColor = "yellow";
      document.body.style.transition = "all 1s";
    }
    else if (clr === 'üîµ') {
      setMode({
        color: 'white',
        backgroundColor: 'blue',
        transition: 'all 1s',
        borderColor: "white"
      });
      // setText("üü¢");
      showAlert("Blue Mode Enabled", "Success");
      document.body.style.backgroundColor = "blue";
      document.body.style.transition = "all 1s";
    }
    else if (text === "‚ö´") {
      setMode({
        color: 'white',
        backgroundColor: 'black',
        transition: 'all 1s',
        borderColor: "white"
      });
      setText("‚òÄÔ∏è");
      showAlert("Dark Mode Enabled", "Success");
      document.body.style.backgroundColor = "black";
      document.body.style.transition = "all 1s";
    }
    else {
      setMode({
        color: 'black',
        backgroundColor: 'white',
        transition: 'all 1s'
        // border: '1px solid black'
      });
      setText("‚ö´");
      showAlert("Light Mode Enabled", "Success");
      document.body.style.backgroundColor = "white"
      document.body.style.transition = "all 1s";

    }
  }
  return (
    <>
      <div>
        <Navbar title='TextUtils' name="Converter" mode={mode} changeMode={setBackground} text={text} />
        <Alert alert={alert} />
        <TextForm heading="Enter the text to convert in UpperCase or LowerCase." mode={mode} text={text} showAlert={showAlert} />
      </div>
    </>
  );
}

export default App;
