import React, {useState} from 'react';



export default function TextForm(props) {
    const [text, setText] = useState("");
    // setText("hey man");
    // console.log(text);
    // let input_text = "hi";
    const handleOnChange = (event)=>{
        // it will handle text value inside the state if any changing occurs inside this
        setText(event.target.value);
    }
    const UpperCase = ()=>{
        setText(text.toUpperCase());
        // console.log("Clicked",text);
    }
    const LowerCase = ()=>{
        setText(text.toLowerCase());
    }
    return (
        <div className='my-4 container'>
            <h2>{props.heading}</h2>
            <div className="input-group">
                <textarea type="text" className="form-control" id="input-text" placeholder="Enter your text" value={text} onChange={handleOnChange} cols="200" rows="5" />
            </div>
            <div className='container'>
                <button className="btn btn-outline-secondary" id='upper-case' type="button" onClick={UpperCase}>UpperCase</button>
                <button className="btn btn-outline-secondary" id='lower-case' type="button" onClick={LowerCase}>LowerCase</button>
                {/* <button className="btn btn-outline-secondary" id='lower-case' type="button" onClick={reset}>Reset</button> */}
                <div className='container'>
                    <h2>Your Text Summary</h2>
                    <h4>Words = <span>{text.split(" ").length}</span> and Characters = <span>{text.length}</span> </h4>
                    <h2 className=''>Preview</h2>
                    <pre className='p-2'>{text}</pre>
                </div>
            </div>

        </div>
    )
}
