        else{
            setText(document.getElementById('lower-case').innerText.toLowerCase());
        }
    }
    return (
        <div className='my-4 container'>
            <h2>{props.heading}</h2>
            <div className="input-group">
                <input type="text" className="form-control input-text" placeholder="Enter your text" aria-label="Recipient's username with two button addons" />
                <button className="btn btn-outline-secondary" id='upper-case' type="button" onClick={caseConverter("l")} >UpperCase</button>
                <button className="btn btn-outline-secondary" id='lower-case' type="button" onClick={caseConverter("u")}>LoweCase</button>
            </div>
            <div className='output'>
                <textarea cols="300" rows="10" value={text}></textarea>
                <input type="button" value="C" title='Copy to clipboard' />
            </div>
        </div>
    )
}
