import { useState } from 'react';
import './App.css';
import './body.css'
import Navbar from './components/Navbar';
import TextForm from './components/TextForm';
function App() {
  const [text, setText] = useState('⚫');
  const [mode, setMode] = useState({
    color: 'black',
    backgroundColor: 'white',
    transition: 'all 1s'
    // border: '1px solid black'
  });
  const setBackground = () => {
    if (text === "⚫") {
      setMode({
        color: 'white',
        backgroundColor: 'black',
        transition: 'all 1s',        
      });
      setText("☀️");
      body.style.backgroun
    }
    else {
      setMode({
        color: 'black',
        backgroundColor: 'white',
        transition: 'all 1s'
        // border: '1px solid black'
      });
      setText("⚫");
    }
  }
  return (
    <>
      <div>
        <Navbar title='TextUtils' name="Converter" mode={mode} changeMode={setBackground} />
        <TextForm heading="Enter the text to convert in UpperCase or LowerCase." mode={mode} />
      </div>
    </>
  );
}

export default App;
