import React, {useState} from 'react';



export default function TextForm(props) {
    const [text, setText] = useState("");
    // setText("hey man");
    // console.log(text);
    // let input_text = "hi";

    // it will handle text value inside the state if any changing occurs inside this
    const handleOnChange = (event)=>{
        setText(event.target.value);
    }
    //to convert given text to UpperCase
    const UpperCase = ()=>{
        setText(text.toUpperCase());
        // console.log("Clicked",text);
    }
    //to convert given text to lowerCase
    const LowerCase = ()=>{
        setText(text.toLowerCase());
    }
    //to convert given text to inverseCase
    const invertCase = ()=>{
        let wordsArray = text.split(" ").filter((num)=>{
            return
        }), newText = ''
        for(let word of wordsArray){
            if(word == (word.toUpperCase())){
                newText += word.toLowerCase()+" ";
            }
            else newText += word.toUpperCase()+" ";
            console.log(word);
        }
        setText(newText);

    }
    //to clear the textArea
    const clear = ()=>{
        setText('');
    }

    return (
        <div className='my-4 container text-center'>
            <h2>{props.heading}</h2>
            <div className="input-group">
                <textarea type="text" className="form-control" id="input-text" placeholder="Enter your text" value={text} onChange={handleOnChange} cols="200" rows="5" />
            </div>
            <div className='container'>
                <button className="btn btn-outline-secondary" id='upper-case' type="button" onClick={UpperCase}>UpperCase</button>
                <button className="btn btn-outline-secondary" id='lower-case' type="button" onClick={LowerCase}>LowerCase</button>
                <button className='btn btn-outline-secondary' type='button' onClick={invertCase}>Invert Case</button>
                <button className="btn btn-outline-danger" id='lower-case' type="button" onClick={clear}>Clear</button>
                <div className='container'>
                    <h2>Your Text Summary</h2>
                    <h4>Words = <span>{text.split(" ").filter((num)=>{
                        return num !== ''
                    }).length}</span> and Characters = <span>{text.length}</span> </h4>
                    <h5><span>{(text.length * 0.005).toFixed(2)}</span> Minutes required to read</h5>
                    <h2 className='mt-4'>Preview</h2>
                    <pre className='p-4 text-start border'>{text}</pre>
                </div>
            </div>

        </div>
    )
}
