import React, {useState} from 'react';

export default function TextForm(props) {
    const [text, setText] = useState("Your output will be here.");
    // setText("hey man");
    // console.log(text);
    const handleOnChange = (event)=>{
        // it will handle text value inside the state if any changing occurs inside this
        setText(event.target.value);
    }
    const UpperCase = ()=>{
        setText(document.getElementById('input-text').value.toUpperCase());
        // console.log("Clicked",text);
    }
    const LowerCase = ()=>{
        setText(document.getElementById('input-text').value.toLowerCase());
        // console.log("clicked", text);
    }
    return (
        <div className='my-4 container'>
            <h2>{props.heading}</h2>
            <div className="input-group d-flex flex-column">
                <textarea type="text" className="form-control" id="input-text" placeholder="Enter your text" cols="'200" rows="10" />
                <button className="btn btn-outline-secondary" id='upper-case' type="button" onClick={UpperCase}>UpperCase</button>
                <button className="btn btn-outline-secondary" id='lower-case' type="button" onClick={LowerCase}>LoweCase</button>
            </div>
            <div className='output'>
                <textarea cols="300" rows="10" value={text} onChange={handleOnChange}></textarea>
                <input type="button" value="C" title='Copy to clipboard' />
            </div>
        </div>
    )
}
